getIdentifierName = function(identifier) {
  return identifier.split("-")[0];
}

getIdentifierVersion = function(identifier) {
  return identifier.split("-")[1].split("_").join(".");
}

exports.getDocNames = function(docs) {
  var docNames = [];
  var docsLength = docs.length;
  for (var i = 0; i < docsLength; i++) {
    var identifierName = getIdentifierName(docs[i]["identifier"]);
    docNames.push(identifierName);
  }
  return docNames;
}

exports.getDocVersions = function(docs, rn) {
  var versions = [];
  var docsLength = docs.length;
  for (var i = 0; i < docsLength; i++) {
    if (getIdentifierName(docs[i]["identifier"]) == rn) {
      var identifierVersion = getIdentifierVersion(docs[i]["identifier"]);
      versions.push(identifierVersion);
    }
  }
  return versions;
}
